let siteName=document.getElementById("nameInput"),siteUrl=document.getElementById("urlInput"),siteList=[];function checkValues(){siteName.value.length>3&&isUrl(siteUrl.value)?getValues(siteName.value,siteUrl.value):siteName.value.length<=3?isUrl(siteUrl.value)?alert("Name must be at least 4 characters long"):alert("Invalid Name & URL"):alert("Invalid URL")}function isUrl(t){return!!/(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&=]*)/.test(t)}function getValues(t,e){clearInputs(),pushToArray({name:t,url:e})}function clearInputs(){siteName.value="",siteUrl.value=""}function pushToArray(t){siteList.unshift(t),saveToLocal(siteList)}function saveToLocal(t){localStorage.setItem("siteList",JSON.stringify(t)),addItem(t)}function addItem(t){let e="";for(let s=0;s<t.length;s++)e+=`
    <tr>
        <td>${s+1}</td>
        <td>${t[s].name}</td>
        <td>
            <a href="${t[s].url}" target="_blank">
                <button class="visit-btn btn text-white">
                <i class="fa-solid fa-eye me-1"></i>
                Visit
                </button>
            </a>
        </td>
        <td>
            <button  onclick=" deleteItem(${s})" class="delete-btn btn text-white">
            <i class="fa-solid fa-trash-can me-1"></i>
            Delete
            </button>            
        </td>
    </tr>
    `;displayItems(e)}function displayItems(t){document.getElementById("content").innerHTML=t}function deleteItem(t){siteList.splice(t,1),saveToLocal(siteList)}!function(){let t=localStorage.getItem("siteList");t&&addItem(siteList=JSON.parse(t))}();